settings.custom_css:
/* Testimonial Section - FULL WIDTH */
selector {
    background-color: #F8F9FA;
    padding: 100px 0;
    max-width: 100% !important;
    width: 100% !important;
}

/* Testimonial Card/Slide - FULL WIDTH */
selector .elementor-testimonial {
    background: #FFFFFF;
    border-radius: 8px;
    padding: 40px 60px;
    box-shadow: 0 4px 20px rgba(0,0,0,0.08);
    max-width: 100%;
}

/* Testimonial Image - ARCH/PILL SHAPE */
selector .elementor-testimonial__image img {
    width: 280px !important;
    height: 350px !important;
    border-radius: 200px 200px 200px 200px;
    object-fit: cover;
    margin-bottom: 25px;
}

/* Name Styling */
selector .elementor-testimonial__name {
    color: #1A3A5C;
    font-family: 'Source Sans Pro', sans-serif;
    font-weight: 700;
    font-size: 18px;
    margin-bottom: 5px;
}

/* Title/Role Styling */
selector .elementor-testimonial__title {
    color: #F4845F;
    font-family: 'Source Sans Pro', sans-serif;
    font-weight: 400;
    font-size: 15px;
    margin-bottom: 15px;
}

/* Quote Text - SMALLER FOR LONG QUOTES */
selector .elementor-testimonial__text {
    color: #4A5568;
    font-family: 'Source Sans Pro', sans-serif;
    font-weight: 400;
    font-size: 15px;
    font-style: normal;
    line-height: 1.7;
    margin-bottom: 20px;
}

/* Navigation Arrows */
selector .elementor-swiper-button {
    color: #1A3A5C;
    font-size: 24px;
}

/* Pagination Dots */
selector .swiper-pagination-bullet {
    background: #E9ECEF;
    opacity: 1;
}

selector .swiper-pagination-bullet-active {
    background: #2A7D7D;
}

/* Force image LEFT, text RIGHT */
selector .elementor-testimonial {
    display: flex !important;
    flex-direction: row !important;
    align-items: center;
    gap: 40px;
    text-align: left;
}

selector .elementor-testimonial__image {
    order: 1;
    flex-shrink: 0;
}

selector .elementor-testimonial__content {
    order: 2;
    flex: 1;
}

/* Keep image size */
selector .elementor-testimonial__image img {
    width: 280px !important;
    height: 350px !important;
    border-radius: 200px;
    object-fit: cover;
}
